mapper.select:
select uuid,type,tool,detail,file_name,scan_id,issue_id,commit_id,repo_id,code_lines,status from rawIssue where tool = #{tool} and commit_id = #{commit_id} and status != 'solved' and status != 'merge solved'
AND repo_id IN
#{repoUuid}

select distinct a.uuid,a.type,a.tool,a.detail,a.file_name,a.scan_id,a.issue_id,a.commit_id,a.repo_id,a.code_lines,a.status ,b.commit_time as commit_time , b.developer_email as developer_email
from rawIssue as a
inner join commit as b
on a.commit_id = b.commit_id
where a.issue_id = #{issue_id}




            or status = #{status}




order by commit_time desc

    limit #{start},#{size}

;

select count(*) from rawIssue where issue_id = #{issueId}



            or status = #{status}



;

select uuid,type,tool,detail,file_name,scan_id,issue_id,commit_id,repo_id,code_lines,status
from rawIssue

    repo_id = #{repo_id}

        and tool = #{tool}

SELECT b.developer_unique_name FROM rawIssue as a INNER JOIN commit_view as b ON a.repo_id = b.repo_id AND a.commit_id = b.commit_id
a.`status` = "solved" AND a.issue_id = #{issue_id}
ORDER BY b.commit_time DESC LIMIT 1

SELECT a.issue_id, b.developer_unique_name as lastSolver, b.commit_id, b.commit_time FROM rawIssue as a INNER JOIN commit_view as b ON a.repo_id = b.repo_id AND a.commit_id = b.commit_id
a.`status` = "solved" AND a.issue_id = #{issue_id}
ORDER BY b.commit_time DESC LIMIT 1

SELECT a.uuid FROM rawIssue AS a INNER JOIN commit_view AS b ON a.commit_id = b.commit_id AND a.repo_id = b.repo_id

    a.issue_id = #{issueUuid}

        AND a.commit_id = #{commit}


ORDER BY b.commit_time DESC LIMIT 1
