mapper.select:
select count(*) from issueTracker.file_measure
where
repo_id = #{repo_id}
and commit_id = #{commit_id}

SELECT file_path,total_lines, repo_id,max(commit_time) as currentModifyTime FROM issueTracker.file_measure
and repo_id in
#{repoUuid}
and commit_time <= #{until}
and total_lines >= 1000
GROUP BY file_path ORDER BY commit_time desc
