mapper.select:
SELECT developer_unique_name, efficiency, quality, contribution, total_level, involved_repo_count, duty_type FROM issueTracker.developer_level


        developer_unique_name in

            #{developer}

SELECT DISTINCT developer FROM issueTracker.commit



            and repo_id in

                #{repoUuid}




            and  commit_time >= #{since} 



            and  commit_time <= #{until}

SELECT developer,repo_id,SUBSTRING(min(commit_time),1,10) AS firstCommitDate FROM issueTracker.commit



            and repo_id = #{repoUuid}



            and  commit_time >= #{since} 



            and  commit_time <= #{until} 



Group By developer

SELECT developer,SUBSTRING(min(commit_time),1,10) AS firstCommitDate FROM issueTracker.commit



            and repo_id = #{repoUuid}



            and  commit_time >= #{since} 



            and  commit_time <= #{until} 

        and developer = #{developer}

SELECT
DISTINCT (repo_uuid)
FROM
issueTracker.commit_view



            and developer_unique_name = #{developer}



            and  commit_time >= #{since} 



            and  commit_time <= #{until}

SELECT distinct(p.repo_uuid),project_name FROM issueTracker.sub_repository as p inner join issueTracker.commit_view as c
WHERE p.repo_uuid = c.repo_uuid
AND c.developer_unique_name = #{developer}

SELECT repo_id,developer,left(commit_time,10) as commit_time,commit_id,message
from issueTracker.commit



            and developer in

                #{gitName}




            and repo_id in

                #{repoUuid}




            and  commit_time >= #{since} 



            and  commit_time <= #{until} 

        and length(parent_commit) < 50


ORDER By commit_time desc

SELECT repo_id,developer,left(commit_time,10) as commit_time,commit_id,message
from issueTracker.commit



            and developer in

                #{gitName}




            and repo_id in

                #{repoUuid}




            and  commit_time >= #{since} 



            and  commit_time <= #{until} 

        and length(parent_commit) < 50


ORDER By commit_time desc
limit ${beginIndex}, ${size}

