SELECT developer_unique_name AS developer_name, COUNT(uuid) AS counts FROM issueTracker.commit_view
and repo_uuid in
#{repoUuid}
and commit_time >= #{since}
and commit_time <= #{until}
GROUP BY developer_unique_name ORDER BY counts DESC
LIMIT 3;

select
COUNT(uuid) as commit_counts
from issueTracker.commit_view



            and developer_unique_name = #{developer}



            and repo_uuid in

                #{repoUuid}




            and  commit_time >= #{since} 



            and  commit_time <= #{until}

SELECT COUNT(uuid) AS commitNum FROM issueTracker.commit


        and repo_id = #{repoUuid}


            and  commit_time >= #{since} 



            and  commit_time <= #{until}
